@import url('https://fonts.cdnfonts.com/css/avenir');

body {
    font-family: 'Avenir', sans-serif !important;
    color: #212529 !important;
}

body .form-section-title {
    /*border-bottom: 1px solid #ced4da;*/
}

body legend, main #form-canvas .form-section-title span, main #form-canvas .subform-section-title span {
    /*text-transform: none;*/
}

body:not(#login) #content > main > div:not(.quickEdit), body:not(#login) #content > main > table {
    position: relative;
     padding: 0px; 
}

body .LabelComponent {
    color: #044e6f;
}

body header.navbar {
    background-color: #fff;
    box-shadow: 0px 3px 13px 0px rgba(0, 0, 0, 0.08) !important;
    height: 70px;
}

body div#header-name img {
    max-height: 48px;
    display: inline-block;
    margin-bottom: 10px;
}

@media (min-width: 768px) {
    body.horizontal_menu.inline_menu #sidebar {
         background: transparent; 
    }
}

body #header-name a#header-link {
    display: inline-block;
    font-size: 16px;
    text-transform: uppercase;
    text-decoration: none;
    vertical-align: sub;
    max-width: 390px;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;
    font-size: 22px;
    word-wrap: break-word;
    color: #000;
    font-weight: 800;
    margin-left: 1rem;
}

body #header-name {
    padding: 13px 0 0;
}

header.navbar .header-nav {
    background-color: #fff;
}

body .header-nav .btn:not(.btn-link), .btn.focus:not(.btn-link), .btn:not(.btn-link):focus, .btn:not(.btn-link):hover, .btn:not(.btn-link):active {
    color: #8e8e8e;
    background-color: transparent;
    box-shadow: none;
}

@media (min-width: 768px) {
    body.horizontal_menu.inline_menu #sidebar .nav > li ul {
        background: #ffffff;
    }
}

@media (min-width: 768px) {
    body.horizontal_menu #main {
        margin-top: 0;
        padding-top: 120px;
    }
}

.container, .container-fluid, .container-lg, .container-md, .container-sm, .container-xl {
    padding: 0 70px;
}

.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    margin-bottom: .5rem;
    font-family: inherit;
    font-weight: 500;
    line-height: 1.2;
}

.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    color: #000;
    font-weight: 500;
    line-height: 1.2;
    margin-bottom: 0.5rem;
    margin-top: 0;
}

.h1, h1 {
    font-size: calc(1.375rem + 1.5vw) !important;
}

body .dataList thead tr {
    background-color: rgb(255 255 255);
    border-bottom: 2px solid #dee2e6
}

body .dataList th, body .dataList th a:hover, body .dataList th a {
    color: #000000;
    text-transform: none;
}

body .dataList table {
    border-bottom: 2px solid #dee2e6;
}

body .dataList td.select_checkbox, body .dataList td.select_radio {
    padding: 8px 15px 8px 10px !important;
}
