properties.css:
body {
    /*font-family: "AvenirMedium";*/
    background-image: url(https://www.gov.rw/typo3conf/ext/risa_gov/Resources/Public/Images/bg_home_sect3.png);
    background-attachment: fixed;
    background-size: cover;
    width: 100%;
    /* overflow-x: hidden; */
}

body .breadcrumb {
    padding: 30px 0px 80px;
    display: none;
}

body.horizontal_menu:not(#login) #page > footer, body.horizontal_menu:not(#login) div#content.page_content, body.embeded div#content.page_content {
    padding-left: 15px;
    padding-right: 15px; 
}


@media (min-width: 576px) {
    body.horizontal_menu:not(#login) #page > footer, body.horizontal_menu:not(#login) div#content.page_content, body.embeded div#content.page_content {
        max-width: 540px;
    }
}

@media (min-width: 768px) {
    body.horizontal_menu:not(#login) #page > footer, body.horizontal_menu:not(#login) div#content.page_content, body.embeded div#content.page_content {
        max-width: 720px;
    }
}

@media (min-width: 992px) {
    body.horizontal_menu:not(#login) #page > footer, body.horizontal_menu:not(#login) div#content.page_content, body.embeded div#content.page_content {
        max-width: 960px;
    }
}

@media (min-width: 1200px) {
    body.horizontal_menu:not(#login) #page > footer, body.horizontal_menu:not(#login) div#content.page_content, body.embeded div#content.page_content {
        max-width: 1140px;
    }
}

body .HeadingComponent {
    font-weight: 700 !important;
}

@media (min-width: 768px) {
    body.horizontal_menu #sidebar {
        width: 100%;
        position: sticky;
        height: auto;
        top: 0;
        margin-bottom: 0px;
        -webkit-transform: initial;
        transform: initial;
        padding: 5px 85px;
        background-color: #fff;
    }
}

/*New*/

body .customized-menu {
    display: flex;
    align-items: center;
}

body .customized-menu li ul {
    background-color: #fff;
    border: 1px solid rgba(0, 0, 0, .15);
    padding: 5px 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 10rem;
    padding: .5rem 0;
    margin: .125rem 0 0;
    font-size: 1rem;
    color: #212529;
    text-align: left;
    list-style: none;
    background-color: #fff;
    background-clip: padding-box;
    border: 1px solid rgba(0, 0, 0, .15);
}

body .menu-container li.menu {
    background-color: #fff;
    transition: all 0.3s;
}

body .customized-dropdown span {

}

body .customized-dropdown span::after {
    content: "\f2f9";
    display: block;
}

body .gov-custom-header .customized-menu .search__wrapper .search__field {
    margin-bottom: 0 !important;
}
