body .gov-custom-header .customized-menu .hiddenBtn:hover {
    transition: all .3 !important;
    box-shadow: none;
}

body .gov-custom-header .customized-menu .hiddenBtn:hover {
    background-color: transparent !important;
    box-shadow: none;
}

body .gov-custom-header .customized-menu .search__icon i {
    color: #5e5e5e !important;
}

body .gov-custom-header .customized-menu .search__icon {
    display: flex;
    justify-content: center; 
    align-items: center;
    background-color: #fff !important;
    height: 32px !important;
    width: 32px !important;
    top: 2px !important;
    right: 2px !important;

}

body .gov-custom-header .customized-menu .search__icon:hover {
    background-color: #fff !important;
    box-shadow: none;
}

body .gov-custom-header .customized-menu .hiddenBtn {
    background-color: transparent !important;
    display: flex;
    justify-content: center;
    align-items: center;
}

body .waves-float {
    -webkit-mask-image: none;
    -webkit-box-shadow: none !important;;
    box-shadow: none !important;
} 

body .gov-custom-header .customized-menu .search__wrapper .search__field {
    transition: all .3s;
}

body .gov-custom-header .customized-menu .search__wrapper .search__field {
    border: 0 !important;
}

body .gov-custom-header .customized-menu .search__wrapper .search__field:focus {
    border: 2px solid #1a88eb !important;
    box-shadow: none !important;
}

@media (min-width: 768px) {
    body.horizontal_menu #sidebar li ul>li>a, body.horizontal_menu #sidebar li ul>li>.sidemenu_inner {
        padding: 4px 20px;
    }
}

@media (min-width: 768px) {
    body.horizontal_menu #sidebar .nav > li ul {
        padding: 8px 0;
    }
}

body .customized-dropdown span::after {
    display: inline-block;
    width: 0;
    height: 0;
    margin-left: .255em;
    vertical-align: .255em;
    content: "";
    border-top: .3em solid;
    border-right: .3em solid transparent;
    border-bottom: 0;
    border-left: .3em solid transparent;
}

@media (min-width: 768px) {
    body.horizontal_menu #sidebar li.toggled>a.dropdown:before, body.horizontal_menu #sidebar li>a.dropdown:before {
        display: inline-block;
        width: 0;
        height: 0;
        margin-left: .255em;
        vertical-align: .255em;
        content: "";
        border-top: .3em solid;
        border-right: .3em solid transparent;
        border-bottom: 0;
        border-left: .3em solid transparent;
        position: absolute;
        right: 0;
        top: 28px;
    }
}

body li.nav-item {
    position: relative;
}

body .customized-menu li ul {
    left: 0;
    position: absolute;
    font-size: 13px;
}

body .customized-dropdown-ul {
    top: 130%;
    z-index: 1000;
    min-width: 150px !important;
    padding: .5rem 0;
    margin: .125rem 0 0;
    font-size: 1rem;
    color: #212529;
    text-align: left;
    list-style: none;
    background-color: #fff;
    background-clip: padding-box;
    border: 1px solid rgba(0, 0, 0, .15);
    border-radius: .25rem;
}

body .customized-dropdown-ul li {
    display: block;
    width: 100%;
    padding: .25rem 1.5rem;
    clear: both;
    font-weight: 400;
    color: #212529;
    text-align: inherit;
    white-space: nowrap;
    background-color: transparent;
    border: 0;
}


body .nav-item {
     padding-left: 0px; 
     font-family: 'avenir'; 
}

body .customized-menu {
    gap: 20px
}

body .nav-link {
    padding: 0; 
}

.customized-dropdown span {

    font-size: 14px;
}

body footer {
    margin: 0 auto;
}

