$(document).ready(function() {
    window.setTimeout(function() {
        $('.hiddenBtn').removeClass('btn waves-effect waves-button waves-float')
        $('.search__icon').removeClass('waves-effect btn waves-button waves-float')
    }, 2000)
})


$(document).click(function (event) {

    // Open dropdown
    $('.customized-dropdown > span').click(function() {
        $('.customized-dropdown-ul').addClass('show')
    })

    // Close dropdown
    if (!$(event.target).is('.customized-dropdown > span')) {
      $('.customized-dropdown-ul').removeClass('show');
    }

    $('.hiddenBtn').click(function (e) {
      e.preventDefault;
      if (
        $('.search__field').hasClass('shrink_search_input') &&
        $('.search__field').val !== ''
      ) {
        $('.search__icon').submit();
      } else {
        $('.search__field').toggleClass('shrink_search_input');
        $('.search__field').focus();
      }

    });
  });
